def parse_date(text):
    date_pattern = re.compile(r"([0-9]{2})-([0-9]{2})-([0-9]{4}) kl. ([0-9]{2}):([0-9]{2})")
    match = re.search(date_pattern, text)

    date = pendulum.datetime(
        int(match.group(3)), 
        int(match.group(2)), 
        int(match.group(1)), 
        int(match.group(4)), 
        int(match.group(5)), 
        0, tz="Europe/Copenhagen"
    ).in_timezone('UTC')

    return date.to_iso8601_string()


def load_meeting(url):
    logger.info("Loading group meeting url: {}", url)
    # Load page
    page = requests.get(url, verify=False).text
    parsed_page = BeautifulSoup(page, "lxml")
    logger.success("Meeting page loaded: {}", url)

    # Get meeting date
    meeting_date_tag = parsed_page.select("span.video_date")[0].text
    meeting_date = parse_date(meeting_date_tag)
    logger.info("Date extracted")

    # Get meeting name
    meeting_name = parsed_page.select("div.video-box-active p")[0].text.strip()
    meeting_slug = slug.slug(meeting_name) + slug.slug(meeting_date)
    logger.info("Name extracted")

    for meeting in meetings:
        if (meeting["name"] == meeting_name) and (meeting["datetime"] == meeting_date):
            logger.warning("Meeting already saved., skipping...")
            return

    logger.info("Extracting video URL...")
    # Get URL to embedded player
    video_iframe_src = parsed_page.select(".video-player-iframe")[0]["src"]

    # Load that page and find entryId
    player_page = requests.get(video_iframe_src).text
    # parsed_player_page = BeautifulSoup(player_page, "lxml")

    entry_id_pattern = re.compile(r"entryId = '([\w]{1,})'")

    # Find entry_id
    entry_id = re.search(entry_id_pattern, player_page).group(1)

    # Find download url
    download_url = "https://cdnapisec.kaltura.com/html5/html5lib/v2.83.1//modules/KalturaSupport/download.php/wid/_2158211/uiconf_id/41051291/entry_id/{}?forceDownload=true&downloadName={}&preferredBitrate=1200".format(entry_id, meeting_slug)
    response = requests.get(download_url, allow_redirects=False)
    video_url = response.headers["Location"]
    logger.success("Video URL extracted")

    logger.info("Generating video thumbnail...")
    # Extract thumbnail
    thumbnail_destination = "/home/simon/nempolitik/static/images/{}.jpg".format(meeting_slug)
    command = "ffmpeg -y -i '{}' -vframes 1 -f image2 '{}'".format(video_url, thumbnail_destination)
    subprocess.call(shlex.split(command), stderr=subprocess.DEVNULL, stdout=subprocess.DEVNULL)
    logger.success("Video thumbnail generated!")

    # Find correct group
    choices = groups.keys()
    key = process.extractOne(meeting_name, choices)[0]
    group_path = groups[key]
    logger.info("Guessed group: {}", key)

    meeting = {
        "datetime": meeting_date,
        "name": meeting_name,
        "video_url": video_url,
        "thumbnail": thumbnail_destination.replace("/home/simon/nempolitik", "")
    }

    fr = frontmatter.load(group_path)
    try:
        fr["meetings"].append(meeting)
    except(KeyError):
        fr["meetings"] = [meeting]

    # Add meeting to group
    with open(group_path, "w+") as file:
        file.write(frontmatter.dumps(fr))

    logger.success("Saved meeting: {}", meeting_name)


